import React, { useState, useEffect } from 'react';
import { 
  Users, 
  CreditCard, 
  Plus, 
  Minus, 
  Search, 
  Filter,
  MoreVertical,
  UserCheck,
  UserX,
  AlertCircle,
  TrendingUp,
  TrendingDown,
  Wallet
} from 'lucide-react';

interface User {
  id: string;
  discordId: string;
  username: string;
  displayName: string;
  credits: number;
  totalSpent: number;
  totalOrders: number;
  level: number;
  referralCode?: string;
  referredBy?: string;
  lastActivity: string;
  joinDate: string;
  status: 'active' | 'inactive' | 'banned';
}

interface Transaction {
  id: string;
  userId: string;
  type: string;
  amount: number;
  description: string;
  timestamp: string;
  adminId?: string;
}

const UserManagement: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [isLoading, setIsLoading] = useState(false);
  const [showAddCredits, setShowAddCredits] = useState(false);
  const [creditAmount, setCreditAmount] = useState<number>(0);
  const [creditReason, setCreditReason] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setIsLoading(true);
    try {
      // Simulated API call - replace with actual API
      const mockUsers: User[] = [
        {
          id: '1',
          discordId: '123456789',
          username: 'samu_resell',
          displayName: 'Samu Resell',
          credits: 125.50,
          totalSpent: 890.25,
          totalOrders: 47,
          level: 15,
          referralCode: 'SAMU2024',
          lastActivity: new Date().toISOString(),
          joinDate: '2024-01-15T10:00:00Z',
          status: 'active'
        },
        {
          id: '2',
          discordId: '987654321',
          username: 'user_example',
          displayName: 'Example User',
          credits: 45.00,
          totalSpent: 230.75,
          totalOrders: 12,
          level: 8,
          referredBy: 'SAMU2024',
          lastActivity: new Date(Date.now() - 86400000).toISOString(),
          joinDate: '2024-02-20T14:30:00Z',
          status: 'active'
        }
      ];

      setUsers(mockUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchUserTransactions = async (userId: string) => {
    try {
      // Simulated API call
      const mockTransactions: Transaction[] = [
        {
          id: '1',
          userId,
          type: 'ricarica_crediti',
          amount: 50.00,
          description: 'Ricarica manuale admin',
          timestamp: new Date().toISOString(),
          adminId: 'admin123'
        },
        {
          id: '2',
          userId,
          type: 'spedizione',
          amount: -5.50,
          description: 'Etichetta UPS 2kg',
          timestamp: new Date(Date.now() - 3600000).toISOString()
        }
      ];

      setTransactions(mockTransactions);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  const handleUserSelect = (user: User) => {
    setSelectedUser(user);
    fetchUserTransactions(user.id);
  };

  const handleAddCredits = async () => {
    if (!selectedUser || !creditAmount || !creditReason) return;

    try {
      // API call to add credits
      const newTransaction: Transaction = {
        id: Date.now().toString(),
        userId: selectedUser.id,
        type: 'ricarica_admin',
        amount: creditAmount,
        description: creditReason,
        timestamp: new Date().toISOString(),
        adminId: 'current-admin'
      };

      // Update user credits
      const updatedUser = {
        ...selectedUser,
        credits: selectedUser.credits + creditAmount
      };

      // Update state
      setUsers(users.map(u => u.id === selectedUser.id ? updatedUser : u));
      setSelectedUser(updatedUser);
      setTransactions([newTransaction, ...transactions]);

      // Reset form
      setCreditAmount(0);
      setCreditReason('');
      setShowAddCredits(false);

      // Show success message
      alert('Crediti aggiunti con successo!');
    } catch (error) {
      console.error('Error adding credits:', error);
      alert('Errore nell\'aggiunta crediti');
    }
  };

  const handleRemoveCredits = async () => {
    if (!selectedUser || !creditAmount || !creditReason) return;

    try {
      const newTransaction: Transaction = {
        id: Date.now().toString(),
        userId: selectedUser.id,
        type: 'rimozione_admin',
        amount: -creditAmount,
        description: creditReason,
        timestamp: new Date().toISOString(),
        adminId: 'current-admin'
      };

      const updatedUser = {
        ...selectedUser,
        credits: Math.max(0, selectedUser.credits - creditAmount)
      };

      setUsers(users.map(u => u.id === selectedUser.id ? updatedUser : u));
      setSelectedUser(updatedUser);
      setTransactions([newTransaction, ...transactions]);

      setCreditAmount(0);
      setCreditReason('');
      setShowAddCredits(false);

      alert('Crediti rimossi con successo!');
    } catch (error) {
      console.error('Error removing credits:', error);
      alert('Errore nella rimozione crediti');
    }
  };

  const filteredUsers = users.filter(user => {
    const matchesSearch = user.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         user.displayName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus === 'all' || user.status === filterStatus;
    return matchesSearch && matchesFilter;
  });

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString('it-IT');
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'text-green-600 bg-green-100';
      case 'inactive': return 'text-yellow-600 bg-yellow-100';
      case 'banned': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getTransactionIcon = (type: string) => {
    if (type.includes('ricarica') || type.includes('bonus')) return <TrendingUp className="w-4 h-4 text-green-500" />;
    if (type.includes('rimozione') || type.includes('spedizione')) return <TrendingDown className="w-4 h-4 text-red-500" />;
    return <CreditCard className="w-4 h-4 text-blue-500" />;
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">Gestione Utenti</h1>
        <p className="text-gray-600">Gestisci utenti, crediti e transazioni</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Lista Utenti */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-sm border">
            <div className="p-4 border-b">
              <div className="flex flex-col sm:flex-row gap-4">
                <div className="relative flex-1">
                  <Search className="w-5 h-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Cerca utenti..."
                    className="w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
                <select
                  className="px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  value={filterStatus}
                  onChange={(e) => setFilterStatus(e.target.value)}
                >
                  <option value="all">Tutti gli stati</option>
                  <option value="active">Attivi</option>
                  <option value="inactive">Inattivi</option>
                  <option value="banned">Bannati</option>
                </select>
              </div>
            </div>

            <div className="divide-y max-h-96 overflow-y-auto">
              {isLoading ? (
                <div className="p-8 text-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
                  <p className="mt-2 text-gray-500">Caricamento utenti...</p>
                </div>
              ) : filteredUsers.length === 0 ? (
                <div className="p-8 text-center">
                  <Users className="w-12 h-12 text-gray-400 mx-auto mb-2" />
                  <p className="text-gray-500">Nessun utente trovato</p>
                </div>
              ) : (
                filteredUsers.map(user => (
                  <div
                    key={user.id}
                    className={`p-4 hover:bg-gray-50 cursor-pointer transition-colors ${
                      selectedUser?.id === user.id ? 'bg-blue-50 border-l-4 border-blue-500' : ''
                    }`}
                    onClick={() => handleUserSelect(user)}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="flex items-center gap-2">
                          <h3 className="font-medium text-gray-900">{user.displayName}</h3>
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(user.status)}`}>
                            {user.status}
                          </span>
                        </div>
                        <p className="text-sm text-gray-500">@{user.username}</p>
                        <div className="flex items-center gap-4 mt-1 text-sm text-gray-600">
                          <span className="flex items-center gap-1">
                            <Wallet className="w-4 h-4" />
                            €{user.credits.toFixed(2)}
                          </span>
                          <span>Livello {user.level}</span>
                          <span>{user.totalOrders} ordini</span>
                        </div>
                      </div>
                      <MoreVertical className="w-5 h-5 text-gray-400" />
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>

        {/* Dettagli Utente */}
        <div>
          {selectedUser ? (
            <div className="space-y-4">
              {/* Info Utente */}
              <div className="bg-white rounded-lg shadow-sm border p-6">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold">Dettagli Utente</h2>
                  <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(selectedUser.status)}`}>
                    {selectedUser.status}
                  </span>
                </div>

                <div className="space-y-3">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Nome Utente</label>
                    <p className="text-gray-900">{selectedUser.displayName}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Discord</label>
                    <p className="text-gray-900">@{selectedUser.username}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Crediti</label>
                    <p className="text-2xl font-bold text-green-600">€{selectedUser.credits.toFixed(2)}</p>
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="text-sm font-medium text-gray-500">Totale Speso</label>
                      <p className="text-gray-900">€{selectedUser.totalSpent.toFixed(2)}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-500">Ordini</label>
                      <p className="text-gray-900">{selectedUser.totalOrders}</p>
                    </div>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Ultima Attività</label>
                    <p className="text-gray-900">{formatDate(selectedUser.lastActivity)}</p>
                  </div>
                </div>
              </div>

              {/* Azioni Crediti */}
              <div className="bg-white rounded-lg shadow-sm border p-6">
                <h3 className="text-lg font-semibold mb-4">Gestione Crediti</h3>

                {!showAddCredits ? (
                  <div className="space-y-2">
                    <button
                      onClick={() => setShowAddCredits(true)}
                      className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                    >
                      <Plus className="w-4 h-4" />
                      Aggiungi Crediti
                    </button>
                    <button
                      onClick={() => setShowAddCredits(true)}
                      className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                    >
                      <Minus className="w-4 h-4" />
                      Rimuovi Crediti
                    </button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Importo (€)
                      </label>
                      <input
                        type="number"
                        step="0.01"
                        min="0"
                        value={creditAmount}
                        onChange={(e) => setCreditAmount(parseFloat(e.target.value) || 0)}
                        className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        placeholder="0.00"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Motivo
                      </label>
                      <input
                        type="text"
                        value={creditReason}
                        onChange={(e) => setCreditReason(e.target.value)}
                        className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        placeholder="Descrivi il motivo..."
                      />
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={handleAddCredits}
                        className="flex-1 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                      >
                        Aggiungi
                      </button>
                      <button
                        onClick={handleRemoveCredits}
                        className="flex-1 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
                      >
                        Rimuovi
                      </button>
                      <button
                        onClick={() => {
                          setShowAddCredits(false);
                          setCreditAmount(0);
                          setCreditReason('');
                        }}
                        className="px-4 py-2 bg-gray-300 text-gray-700 rounded-lg hover:bg-gray-400 transition-colors"
                      >
                        Annulla
                      </button>
                    </div>
                  </div>
                )}
              </div>

              {/* Transazioni Recenti */}
              <div className="bg-white rounded-lg shadow-sm border p-6">
                <h3 className="text-lg font-semibold mb-4">Transazioni Recenti</h3>
                <div className="space-y-3 max-h-64 overflow-y-auto">
                  {transactions.length === 0 ? (
                    <p className="text-gray-500 text-center py-4">Nessuna transazione</p>
                  ) : (
                    transactions.map(transaction => (
                      <div key={transaction.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                        <div className="flex items-center gap-3">
                          {getTransactionIcon(transaction.type)}
                          <div>
                            <p className="font-medium text-sm">{transaction.description}</p>
                            <p className="text-xs text-gray-500">{formatDate(transaction.timestamp)}</p>
                          </div>
                        </div>
                        <span className={`font-bold ${transaction.amount >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {transaction.amount >= 0 ? '+' : ''}€{transaction.amount.toFixed(2)}
                        </span>
                      </div>
                    ))
                  )}
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-white rounded-lg shadow-sm border p-6 text-center">
              <UserCheck className="w-12 h-12 text-gray-400 mx-auto mb-2" />
              <p className="text-gray-500">Seleziona un utente per vedere i dettagli</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserManagement;
