import React, { useState, useEffect } from 'react';
import {
  TrendingUp,
  TrendingDown,
  Brain,
  Target,
  Clock,
  Calendar,
  Users,
  Package,
  DollarSign,
  AlertTriangle,
  CheckCircle,
  BarChart3,
  PieChart,
  Activity,
  Zap,
  Star
} from 'lucide-react';

interface AnalyticsData {
  revenue: {
    today: number;
    week: number;
    month: number;
    growth: number;
  };
  orders: {
    total: number;
    completed: number;
    pending: number;
    failed: number;
    avgProcessingTime: number;
  };
  users: {
    total: number;
    active: number;
    new: number;
    retention: number;
  };
  carriers: {
    ups: { count: number; revenue: number; avgTime: number };
    colissimo: { count: number; revenue: number; avgTime: number };
  };
  predictions: {
    dailyOrders: number;
    weeklyRevenue: number;
    peakHours: string[];
    recommendations: string[];
  };
  aiInsights: {
    performanceScore: number;
    efficiency: number;
    recommendations: AiRecommendation[];
    alerts: AiAlert[];
  };
}

interface AiRecommendation {
  id: string;
  type: 'optimization' | 'alert' | 'opportunity';
  title: string;
  description: string;
  impact: 'high' | 'medium' | 'low';
  confidence: number;
}

interface AiAlert {
  id: string;
  severity: 'critical' | 'warning' | 'info';
  title: string;
  message: string;
  timestamp: string;
}

const AdvancedAnalytics: React.FC = () => {
  const [analyticsData, setAnalyticsData] = useState<AnalyticsData | null>(null);
  const [timeRange, setTimeRange] = useState<'day' | 'week' | 'month'>('week');
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'overview' | 'ai-insights' | 'predictions'>('overview');

  useEffect(() => {
    fetchAnalyticsData();
    const interval = setInterval(fetchAnalyticsData, 30000); // Update every 30 seconds
    return () => clearInterval(interval);
  }, [timeRange]);

  const fetchAnalyticsData = async () => {
    try {
      // Simulate AI-powered analytics data
      const mockData: AnalyticsData = {
        revenue: {
          today: 234.50,
          week: 1567.25,
          month: 6890.75,
          growth: 12.5
        },
        orders: {
          total: 1247,
          completed: 1198,
          pending: 34,
          failed: 15,
          avgProcessingTime: 145 // seconds
        },
        users: {
          total: 342,
          active: 187,
          new: 23,
          retention: 78.5
        },
        carriers: {
          ups: { count: 789, revenue: 4234.50, avgTime: 120 },
          colissimo: { count: 458, revenue: 2656.25, avgTime: 180 }
        },
        predictions: {
          dailyOrders: 67,
          weeklyRevenue: 1789.50,
          peakHours: ['09:00-11:00', '14:00-16:00', '19:00-21:00'],
          recommendations: [
            'Ottimizza le fasce orarie 14:00-16:00 per aumentare l\'efficienza',
            'Considera promozioni durante le ore meno trafficate',
            'Implementa automazioni per gestire i picchi di traffico'
          ]
        },
        aiInsights: {
          performanceScore: 87,
          efficiency: 92,
          recommendations: [
            {
              id: '1',
              type: 'optimization',
              title: 'Ottimizzazione Pricing Dinamico',
              description: 'L\'AI suggerisce di aumentare i prezzi UPS del 8% durante i picchi di domanda per massimizzare i ricavi',
              impact: 'high',
              confidence: 94
            },
            {
              id: '2',
              type: 'opportunity',
              title: 'Opportunità Weekend',
              description: 'Analisi mostra 23% meno ordini nei weekend. Implementa promozioni mirate per aumentare il volume',
              impact: 'medium',
              confidence: 78
            },
            {
              id: '3',
              type: 'alert',
              title: 'Tempo Elaborazione Anomalo',
              description: 'Rilevato aumento del 15% nei tempi di elaborazione Colissimo nelle ultime 48h',
              impact: 'medium',
              confidence: 89
            }
          ],
          alerts: [
            {
              id: '1',
              severity: 'warning',
              title: 'Capacità Sistema',
              message: 'Il sistema sta raggiungendo l\'85% della capacità nelle ore di punta',
              timestamp: new Date().toISOString()
            },
            {
              id: '2',
              severity: 'info',
              title: 'Crescita Utenti',
              message: 'Rilevata crescita del 15% di nuovi utenti questa settimana',
              timestamp: new Date(Date.now() - 3600000).toISOString()
            }
          ]
        }
      };

      setAnalyticsData(mockData);
    } catch (error) {
      console.error('Error fetching analytics:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const formatCurrency = (amount: number) => `€${amount.toFixed(2)}`;
  const formatPercent = (value: number) => `${value.toFixed(1)}%`;

  const getImpactColor = (impact: string) => {
    switch (impact) {
      case 'high': return 'text-red-600 bg-red-100';
      case 'medium': return 'text-yellow-600 bg-yellow-100';
      case 'low': return 'text-green-600 bg-green-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'text-red-600 bg-red-100 border-red-200';
      case 'warning': return 'text-yellow-600 bg-yellow-100 border-yellow-200';
      case 'info': return 'text-blue-600 bg-blue-100 border-blue-200';
      default: return 'text-gray-600 bg-gray-100 border-gray-200';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical': return <AlertTriangle className="w-4 h-4" />;
      case 'warning': return <Clock className="w-4 h-4" />;
      case 'info': return <CheckCircle className="w-4 h-4" />;
      default: return <Activity className="w-4 h-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="p-6 max-w-7xl mx-auto">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
          <p className="ml-4 text-gray-600">Caricamento analytics...</p>
        </div>
      </div>
    );
  }

  if (!analyticsData) return null;

  return (
