import React, { useState, useEffect } from 'react';
import { 
  Bell, 
  X, 
  Check, 
  AlertCircle, 
  Info, 
  CheckCircle, 
  AlertTriangle,
  Package,
  Users,
  Activity,
  Settings,
  Trash2,
  Filter
} from 'lucide-react';
import { useWebSocket, NotificationData, OrderUpdateData, UserActivityData } from '../hooks/useWebSocket';

interface NotificationCenterProps {
  isOpen: boolean;
  onClose: () => void;
}

const NotificationCenter: React.FC<NotificationCenterProps> = ({ isOpen, onClose }) => {
  const { 
    notifications, 
    orderUpdates, 
    userActivity, 
    isConnected, 
    systemStatus,
    clearNotifications, 
    markNotificationAsRead 
  } = useWebSocket();

  const [activeTab, setActiveTab] = useState<'notifications' | 'orders' | 'activity'>('notifications');
  const [filter, setFilter] = useState<'all' | 'unread' | 'success' | 'warning' | 'error'>('all');

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'success': return <CheckCircle className="w-5 h-5 text-green-500" />;
      case 'warning': return <AlertTriangle className="w-5 h-5 text-yellow-500" />;
      case 'error': return <AlertCircle className="w-5 h-5 text-red-500" />;
      case 'info': return <Info className="w-5 h-5 text-blue-500" />;
      default: return <Bell className="w-5 h-5 text-gray-500" />;
    }
  };

  const getOrderStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'completed': return 'text-green-600 bg-green-100';
      case 'processing': return 'text-blue-600 bg-blue-100';
      case 'pending': return 'text-yellow-600 bg-yellow-100';
      case 'failed': return 'text-red-600 bg-red-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'login': return <Users className="w-4 h-4 text-blue-500" />;
      case 'order_created': return <Package className="w-4 h-4 text-green-500" />;
      case 'credits_added': return <CheckCircle className="w-4 h-4 text-purple-500" />;
      case 'logout': return <Users className="w-4 h-4 text-gray-500" />;
      default: return <Activity className="w-4 h-4 text-gray-500" />;
    }
  };

  const filteredNotifications = notifications.filter(notif => {
    if (filter === 'all') return true;
    if (filter === 'unread') return !notif.read;
    return notif.type === filter;
  });

  const formatTime = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now.getTime() - date.getTime();

    if (diff < 60000) return 'Ora';
    if (diff < 3600000) return `${Math.floor(diff / 60000)}m fa`;
    if (diff < 86400000) return `${Math.floor(diff / 3600000)}h fa`;
    return date.toLocaleDateString('it-IT');
  };

  // Request notification permission on component mount
  useEffect(() => {
    if (Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, []);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-hidden">
      <div className="absolute inset-0 bg-black bg-opacity-25" onClick={onClose} />

      <div className="absolute right-0 top-0 h-full w-full max-w-md bg-white shadow-2xl">
        <div className="flex h-full flex-col">
          {/* Header */}
          <div className="border-b bg-white px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Bell className="w-6 h-6 text-blue-600" />
                <h2 className="text-lg font-semibold">Centro Notifiche</h2>
                <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-400' : 'bg-red-400'}`} />
              </div>
              <button
                onClick={onClose}
                className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100"
              >
                <X className="w-5 h-5" />
              </button>
            </div>

            {/* System Status */}
            {systemStatus && (
              <div className="mt-3 p-2 bg-gray-50 rounded-lg">
                <div className="flex items-center justify-between text-sm">
                  <span>Sistema: {systemStatus.status}</span>
                  <span>AURA: {systemStatus.auraActive ? '✅' : '❌'}</span>
                  <span>Load: {systemStatus.systemLoad}%</span>
                </div>
              </div>
            )}

            {/* Tabs */}
            <div className="flex mt-4 space-x-1 bg-gray-100 rounded-lg p-1">
              <button
                onClick={() => setActiveTab('notifications')}
                className={`flex-1 py-2 px-3 text-sm font-medium rounded-md transition-colors ${
                  activeTab === 'notifications'
                    ? 'bg-white text-blue-600 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                Notifiche ({notifications.length})
              </button>
              <button
                onClick={() => setActiveTab('orders')}
                className={`flex-1 py-2 px-3 text-sm font-medium rounded-md transition-colors ${
                  activeTab === 'orders'
                    ? 'bg-white text-blue-600 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                Ordini ({orderUpdates.length})
              </button>
              <button
                onClick={() => setActiveTab('activity')}
                className={`flex-1 py-2 px-3 text-sm font-medium rounded-md transition-colors ${
                  activeTab === 'activity'
                    ? 'bg-white text-blue-600 shadow-sm'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                Attività ({userActivity.length})
              </button>
            </div>
          </div>

          {/* Filters and Actions */}
          <div className="border-b bg-gray-50 px-6 py-3">
            <div className="flex items-center justify-between">
              {activeTab === 'notifications' && (
                <select
                  value={filter}
                  onChange={(e) => setFilter(e.target.value as any)}
                  className="text-sm border rounded px-2 py-1"
                >
                  <option value="all">Tutte</option>
                  <option value="unread">Non lette</option>
                  <option value="success">Successo</option>
                  <option value="warning">Avvisi</option>
                  <option value="error">Errori</option>
                </select>
              )}

              <div className="flex gap-2">
                {activeTab === 'notifications' && (
                  <button
                    onClick={clearNotifications}
                    className="p-1 text-gray-500 hover:text-red-600 rounded"
                    title="Cancella tutte"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                )}
              </div>
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto">
            {/* Notifications Tab */}
            {activeTab === 'notifications' && (
              <div className="divide-y">
                {filteredNotifications.length === 0 ? (
                  <div className="p-6 text-center text-gray-500">
                    <Bell className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                    <p>Nessuna notifica</p>
                  </div>
                ) : (
                  filteredNotifications.map((notification) => (
                    <div
                      key={notification.id}
                      className={`p-4 hover:bg-gray-50 ${!notification.read ? 'bg-blue-50' : ''}`}
                    >
                      <div className="flex gap-3">
                        {getNotificationIcon(notification.type)}
                        <div className="flex-1 min-w-0">
                          <div className="flex items-start justify-between">
                            <h4 className="text-sm font-medium text-gray-900 truncate">
                              {notification.title}
                            </h4>
                            <div className="flex items-center gap-2 ml-2">
                              <span className="text-xs text-gray-500">
                                {formatTime(notification.timestamp)}
                              </span>
                              {!notification.read && (
                                <button
                                  onClick={() => markNotificationAsRead(notification.id)}
                                  className="p-1 text-blue-600 hover:bg-blue-100 rounded"
                                >
                                  <Check className="w-3 h-3" />
                                </button>
                              )}
                            </div>
                          </div>
                          <p className="text-sm text-gray-600 mt-1">{notification.message}</p>
                          {notification.orderId && (
                            <div className="mt-2 text-xs text-gray-500">
                              Ordine: {notification.orderId.substring(0, 8)}...
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}

            {/* Orders Tab */}
            {activeTab === 'orders' && (
              <div className="divide-y">
                {orderUpdates.length === 0 ? (
                  <div className="p-6 text-center text-gray-500">
                    <Package className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                    <p>Nessun aggiornamento ordini</p>
                  </div>
                ) : (
                  orderUpdates.map((order, index) => (
                    <div key={`${order.orderId}-${index}`} className="p-4 hover:bg-gray-50">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <Package className="w-4 h-4 text-blue-500" />
                            <span className="text-sm font-medium">
                              {order.orderId.substring(0, 12)}...
                            </span>
                            <span className={`px-2 py-1 text-xs font-medium rounded-full ${getOrderStatusColor(order.status)}`}>
                              {order.status}
                            </span>
                          </div>
                          <p className="text-sm text-gray-600">@{order.username}</p>
                          <div className="text-xs text-gray-500 mt-1">
                            {order.details.carrier.toUpperCase()} • {order.details.weight}
                            {order.details.tracking && (
                              <span className="ml-2">• Tracking: {order.details.tracking}</span>
                            )}
                          </div>
                        </div>
                        <span className="text-xs text-gray-500">
                          {formatTime(order.timestamp)}
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}

            {/* Activity Tab */}
            {activeTab === 'activity' && (
              <div className="divide-y">
                {userActivity.length === 0 ? (
                  <div className="p-6 text-center text-gray-500">
                    <Activity className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                    <p>Nessuna attività</p>
                  </div>
                ) : (
                  userActivity.map((activity, index) => (
                    <div key={`${activity.userId}-${index}`} className="p-4 hover:bg-gray-50">
                      <div className="flex items-start gap-3">
                        {getActivityIcon(activity.type)}
                        <div className="flex-1">
                          <div className="flex items-center justify-between">
                            <span className="text-sm font-medium">@{activity.username}</span>
                            <span className="text-xs text-gray-500">
                              {formatTime(activity.timestamp)}
                            </span>
                          </div>
                          <p className="text-sm text-gray-600 mt-1">
                            {activity.type === 'login' && 'Si è connesso alla dashboard'}
                            {activity.type === 'order_created' && `Ha creato un ordine ${activity.details?.carrier || ''}`}
                            {activity.type === 'credits_added' && `Ha ricevuto €${activity.details?.amount || '0'} crediti`}
                            {activity.type === 'logout' && 'Si è disconnesso dalla dashboard'}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="border-t bg-gray-50 px-6 py-3">
            <div className="flex items-center justify-between text-xs text-gray-500">
              <span>
                {isConnected ? (
                  <span className="flex items-center gap-1">
                    <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                    Live • Connesso
                  </span>
                ) : (
                  <span className="flex items-center gap-1">
                    <div className="w-2 h-2 bg-red-400 rounded-full" />
                    Disconnesso • Riconnessione...
                  </span>
                )}
              </span>
              <button className="flex items-center gap-1 hover:text-gray-700">
                <Settings className="w-3 h-3" />
                Impostazioni
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NotificationCenter;
