import React, { useState, useEffect, useRef, Fragment } from 'react';
import { Dialog, Transition } from '@headlessui/react';
import { MagnifyingGlassIcon, CommandLineIcon } from '@heroicons/react/24/outline';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

interface Command {
  id: string;
  title: string;
  subtitle?: string;
  action: () => void;
  icon?: React.ElementType;
  keywords: string[];
  category: 'navigation' | 'actions' | 'bot' | 'system';
}

interface CommandPaletteProps {
  open: boolean;
  onClose: () => void;
}

const CommandPalette: React.FC<CommandPaletteProps> = ({ open, onClose }) => {
  const [query, setQuery] = useState('');
  const [selectedIndex, setSelectedIndex] = useState(0);
  const navigate = useNavigate();
  const inputRef = useRef<HTMLInputElement>(null);

  const commands: Command[] = [
    // Navigation
    {
      id: 'nav-dashboard',
      title: 'Dashboard Overview',
      subtitle: 'Vai alla dashboard principale',
      action: () => navigate('/'),
      keywords: ['dashboard', 'home', 'overview', 'principale'],
      category: 'navigation'
    },
    {
      id: 'nav-shipping',
      title: 'Shipping Management',
      subtitle: 'Gestione spedizioni e ordini',
      action: () => navigate('/shipping'),
      keywords: ['shipping', 'spedizioni', 'ordini', 'etichette'],
      category: 'navigation'
    },
    {
      id: 'nav-users',
      title: 'Gestione Utenti',
      subtitle: 'Visualizza e gestisci utenti bot',
      action: () => navigate('/users'),
      keywords: ['users', 'utenti', 'gestione'],
      category: 'navigation'
    },
    {
      id: 'nav-finance',
      title: 'Financial Overview',
      subtitle: 'Controllo costi e budget',
      action: () => navigate('/finance'),
      keywords: ['finance', 'finanza', 'costi', 'budget'],
      category: 'navigation'
    },
    {
      id: 'nav-analytics',
      title: 'Analytics',
      subtitle: 'Report e statistiche avanzate',
      action: () => navigate('/analytics'),
      keywords: ['analytics', 'statistiche', 'report'],
      category: 'navigation'
    },
    {
      id: 'nav-logs',
      title: 'System Logs',
      subtitle: 'Visualizza log di sistema',
      action: () => navigate('/logs'),
      keywords: ['logs', 'debug', 'errori'],
      category: 'navigation'
    },

    // Actions
    {
      id: 'action-restart-bot',
      title: 'Restart Bot',
      subtitle: 'Riavvia il bot Discord',
      action: () => {
        // TODO: Implementare restart bot
        console.log('Restart bot triggered');
      },
      keywords: ['restart', 'riavvia', 'bot'],
      category: 'bot'
    },
    {
      id: 'action-deploy-commands',
      title: 'Deploy Commands',
      subtitle: 'Aggiorna comandi Discord',
      action: () => {
        // TODO: Implementare deploy commands
        console.log('Deploy commands triggered');
      },
      keywords: ['deploy', 'commands', 'comandi', 'aggiorna'],
      category: 'bot'
    },
    {
      id: 'action-refresh-data',
      title: 'Refresh Dashboard',
      subtitle: 'Aggiorna tutti i dati',
      action: () => {
        window.location.reload();
      },
      keywords: ['refresh', 'aggiorna', 'reload'],
      category: 'system'
    },
    {
      id: 'action-export-orders',
      title: 'Export Orders',
      subtitle: 'Esporta ordini in CSV',
      action: () => {
        // TODO: Implementare export
        console.log('Export orders triggered');
      },
      keywords: ['export', 'esporta', 'csv', 'ordini'],
      category: 'actions'
    }
  ];

  const filteredCommands = query === ''
    ? commands
    : commands.filter((command) => {
        const searchText = `${command.title} ${command.subtitle} ${command.keywords.join(' ')}`.toLowerCase();
        return searchText.includes(query.toLowerCase());
      });

  const executeCommand = (command: Command) => {
    command.action();
    onClose();
    setQuery('');
    setSelectedIndex(0);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setSelectedIndex((prev) => 
          prev < filteredCommands.length - 1 ? prev + 1 : 0
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setSelectedIndex((prev) => 
          prev > 0 ? prev - 1 : filteredCommands.length - 1
        );
        break;
      case 'Enter':
        e.preventDefault();
        if (filteredCommands[selectedIndex]) {
          executeCommand(filteredCommands[selectedIndex]);
        }
        break;
      case 'Escape':
        onClose();
        break;
    }
  };

  useEffect(() => {
    if (open && inputRef.current) {
      inputRef.current.focus();
    }
    setSelectedIndex(0);
  }, [open]);

  useEffect(() => {
    setSelectedIndex(0);
  }, [query]);

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'navigation':
        return 'ðŸ§­';
      case 'actions':
        return 'âš¡';
      case 'bot':
        return 'ðŸ¤–';
      case 'system':
        return 'âš™ï¸';
      default:
        return 'ðŸ“';
    }
  };

  const groupedCommands = filteredCommands.reduce((acc, command) => {
    if (!acc[command.category]) {
      acc[command.category] = [];
    }
    acc[command.category].push(command);
    return acc;
  }, {} as Record<string, Command[]>);

  return (
    <Transition appear show={open} as={Fragment}>
      <Dialog as="div" className="relative z-50" onClose={onClose}>
        <Transition.Child
          as={Fragment}
          enter="ease-out duration-300"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="ease-in duration-200"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black bg-opacity-25 backdrop-blur-sm" />
        </Transition.Child>

        <div className="fixed inset-0 overflow-y-auto">
          <div className="flex min-h-full items-start justify-center p-4 text-center sm:p-8 pt-[10vh]">
            <Transition.Child
              as={Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0 scale-95"
              enterTo="opacity-100 scale-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100 scale-100"
              leaveTo="opacity-0 scale-95"
            >
              <Dialog.Panel className="w-full max-w-lg transform overflow-hidden rounded-xl bg-white dark:bg-gray-800 shadow-2xl transition-all">
                {/* Header */}
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" />
                  </div>
                  <input
                    ref={inputRef}
                    type="text"
                    className="command-input w-full pl-10 pr-4 py-4 text-gray-900 dark:text-gray-100 placeholder-gray-500 bg-transparent border-0 focus:ring-0"
                    placeholder="Digita un comando o cerca..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    onKeyDown={handleKeyDown}
                  />
                  <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                    <div className="flex items-center space-x-1 text-xs text-gray-400">
                      <kbd className="px-1.5 py-0.5 rounded bg-gray-100 dark:bg-gray-700">â†‘â†“</kbd>
                      <kbd className="px-1.5 py-0.5 rounded bg-gray-100 dark:bg-gray-700">â†µ</kbd>
                    </div>
                  </div>
                </div>

                {/* Results */}
                <div className="border-t border-gray-200 dark:border-gray-700 max-h-96 overflow-y-auto custom-scrollbar">
                  {Object.keys(groupedCommands).length === 0 ? (
                    <div className="p-8 text-center">
                      <CommandLineIcon className="mx-auto h-12 w-12 text-gray-400" />
                      <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-gray-100">
                        Nessun comando trovato
                      </h3>
                      <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                        Prova con un termine diverso
                      </p>
                    </div>
                  ) : (
                    <div className="py-2">
                      {Object.entries(groupedCommands).map(([category, commands], categoryIndex) => (
                        <div key={category}>
                          {Object.keys(groupedCommands).length > 1 && (
                            <div className="px-4 py-2 text-xs font-medium text-gray-500 dark:text-gray-400 uppercase tracking-wider flex items-center space-x-2">
                              <span>{getCategoryIcon(category)}</span>
                              <span>{category}</span>
                            </div>
                          )}

                          {commands.map((command, commandIndex) => {
                            const globalIndex = Object.entries(groupedCommands)
                              .slice(0, categoryIndex)
                              .reduce((acc, [, cmds]) => acc + cmds.length, 0) + commandIndex;

                            return (
                              <motion.button
                                key={command.id}
                                onClick={() => executeCommand(command)}
                                className={`w-full px-4 py-3 text-left flex items-center space-x-3 hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-colors ${
                                  selectedIndex === globalIndex
                                    ? 'bg-primary-50 dark:bg-primary-900/20 border-r-2 border-primary-500'
                                    : ''
                                }`}
                                whileHover={{ x: 2 }}
                                transition={{ duration: 0.1 }}
                              >
                                <div className="flex-1 min-w-0">
                                  <div className="text-sm font-medium text-gray-900 dark:text-gray-100">
                                    {command.title}
                                  </div>
                                  {command.subtitle && (
                                    <div className="text-xs text-gray-500 dark:text-gray-400 truncate">
                                      {command.subtitle}
                                    </div>
                                  )}
                                </div>

                                {selectedIndex === globalIndex && (
                                  <div className="flex-shrink-0 text-xs text-primary-600 dark:text-primary-400">
                                    â†µ
                                  </div>
                                )}
                              </motion.button>
                            );
                          })}
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                {/* Footer */}
                <div className="border-t border-gray-200 dark:border-gray-700 px-4 py-3 bg-gray-50 dark:bg-gray-700/50">
                  <div className="flex items-center justify-between text-xs text-gray-500 dark:text-gray-400">
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center space-x-1">
                        <kbd className="px-1.5 py-0.5 rounded bg-white dark:bg-gray-600 text-gray-700 dark:text-gray-300">â†µ</kbd>
                        <span>per eseguire</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <kbd className="px-1.5 py-0.5 rounded bg-white dark:bg-gray-600 text-gray-700 dark:text-gray-300">Esc</kbd>
                        <span>per chiudere</span>
                      </div>
                    </div>
                    <div className="flex items-center space-x-1">
                      <span>âŒ˜K per aprire</span>
                    </div>
                  </div>
                </div>
              </Dialog.Panel>
            </Transition.Child>
          </div>
        </div>
      </Dialog>
    </Transition>
  );
};

export default CommandPalette;
