import axios, { AxiosInstance, AxiosRequestConfig } from 'axios';
import { 
  ApiResponse, 
  User, 
  BotStatus, 
  ShippingOrder, 
  ShippingStats, 
  QueueStatus, 
  FinancialData, 
  BotUser, 
  SystemHealth, 
  LogEntry,
  PaginatedResponse,
  KPI,
  Alert
} from '../types';
import toast from 'react-hot-toast';

// Configurazione base API
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000/api';

class ApiClient {
  private client: AxiosInstance;
  private token: string | null = null;

  constructor() {
    this.client = axios.create({
      baseURL: API_BASE_URL,
      timeout: 30000,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    this.setupInterceptors();
  }

  private setupInterceptors() {
    // Request interceptor - aggiunge token
    this.client.interceptors.request.use(
      (config) => {
        const token = this.token || localStorage.getItem('auth_token');
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => Promise.reject(error)
    );

    // Response interceptor - gestisce errori globali
    this.client.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          localStorage.removeItem('auth_token');
          window.location.href = '/login';
        }

        if (error.response?.status >= 500) {
          toast.error('Errore del server. Riprova più tardi.');
        }

        return Promise.reject(error);
      }
    );
  }

  setToken(token: string) {
    this.token = token;
  }

  private async request<T>(config: AxiosRequestConfig): Promise<T> {
    const response = await this.client.request<ApiResponse<T>>(config);

    if (!response.data.success) {
      throw new Error(response.data.message || 'Errore API');
    }

    return response.data.data!;
  }

  // ===== AUTH ENDPOINTS =====
  async login(email: string, password: string): Promise<{ user: User; token: string }> {
    return this.request({
      method: 'POST',
      url: '/auth/login',
      data: { email, password }
    });
  }

  async verifyToken(token: string): Promise<User> {
    return this.request({
      method: 'GET',
      url: '/auth/verify',
      headers: { Authorization: `Bearer ${token}` }
    });
  }

  async refreshToken(token: string): Promise<{ user: User; token: string }> {
    return this.request({
      method: 'POST',
      url: '/auth/refresh',
      headers: { Authorization: `Bearer ${token}` }
    });
  }

  // ===== DASHBOARD ENDPOINTS =====
  async getDashboardOverview(): Promise<{
    kpis: KPI[];
    recentActivity: any[];
    systemHealth: SystemHealth;
    alerts: Alert[];
  }> {
    return this.request({
      method: 'GET',
      url: '/web/dashboard/overview'
    });
  }

  // ===== BOT MANAGEMENT =====
  async getBotStatus(): Promise<BotStatus> {
    return this.request({
      method: 'GET',
      url: '/web/bot/status'
    });
  }

  async restartBot(): Promise<{ success: boolean; message: string }> {
    return this.request({
      method: 'POST',
      url: '/web/bot/restart'
    });
  }

  async deployCommands(): Promise<{ success: boolean; message: string }> {
    return this.request({
      method: 'POST',
      url: '/web/bot/deploy-commands'
    });
  }

  async sendBotMessage(channelId: string, message: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'POST',
      url: '/web/bot/send-message',
      data: { channelId, message }
    });
  }

  // ===== SHIPPING MANAGEMENT =====
  async getShippingStats(dateRange?: { start: string; end: string }): Promise<ShippingStats> {
    return this.request({
      method: 'GET',
      url: '/web/shipping/stats',
      params: dateRange
    });
  }

  async getQueueStatus(): Promise<QueueStatus> {
    return this.request({
      method: 'GET',
      url: '/shipping/queue'
    });
  }

  async getShippingOrders(params: {
    page?: number;
    limit?: number;
    status?: string;
    userId?: string;
    dateRange?: { start: string; end: string };
  }): Promise<PaginatedResponse<ShippingOrder>> {
    const response = await this.client.get('/web/shipping/orders', { params });
    return response.data;
  }

  async cancelShippingOrder(orderId: string): Promise<{ success: boolean; message: string }> {
    return this.request({
      method: 'DELETE',
      url: `/shipping/orders/${orderId}`
    });
  }

  async getShippingAnalytics(): Promise<{
    hourlyDistribution: Array<{ hour: number; orders: number }>;
    carrierPerformance: Array<{ carrier: string; avgTime: number; successRate: number }>;
    geographicDistribution: Array<{ country: string; orders: number; revenue: number }>;
    trendsAnalysis: Array<{ date: string; orders: number; revenue: number }>;
  }> {
    return this.request({
      method: 'GET',
      url: '/web/shipping/analytics'
    });
  }

  // ===== FINANCIAL MANAGEMENT =====
  async getFinancialData(): Promise<FinancialData> {
    return this.request({
      method: 'GET',
      url: '/web/finance/overview'
    });
  }

  async updateBudgetLimits(limits: {
    daily: number;
    weekly: number;
    monthly: number;
  }): Promise<{ success: boolean }> {
    return this.request({
      method: 'PUT',
      url: '/web/finance/budget-limits',
      data: limits
    });
  }

  async acknowledgeFinancialAlert(alertId: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'POST',
      url: `/web/finance/alerts/${alertId}/acknowledge`
    });
  }

  // ===== USER MANAGEMENT =====
  async getBotUsers(params: {
    page?: number;
    limit?: number;
    search?: string;
    sortBy?: string;
    orderBy?: 'asc' | 'desc';
  }): Promise<PaginatedResponse<BotUser>> {
    const response = await this.client.get('/web/users', { params });
    return response.data;
  }

  async getBotUser(userId: string): Promise<BotUser> {
    return this.request({
      method: 'GET',
      url: `/web/users/${userId}`
    });
  }

  async updateUserCredits(userId: string, amount: number, reason: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'PUT',
      url: `/web/users/${userId}/credits`,
      data: { amount, reason }
    });
  }

  async blockUser(userId: string, reason: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'POST',
      url: `/web/users/${userId}/block`,
      data: { reason }
    });
  }

  async unblockUser(userId: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'POST',
      url: `/web/users/${userId}/unblock`
    });
  }

  // ===== LOGS & MONITORING =====
  async getLogs(params: {
    page?: number;
    limit?: number;
    level?: string;
    source?: string;
    search?: string;
    dateRange?: { start: string; end: string };
  }): Promise<PaginatedResponse<LogEntry>> {
    const response = await this.client.get('/web/logs', { params });
    return response.data;
  }

  async getSystemHealth(): Promise<SystemHealth> {
    return this.request({
      method: 'GET',
      url: '/web/system/health'
    });
  }

  async getAlerts(acknowledged: boolean = false): Promise<Alert[]> {
    return this.request({
      method: 'GET',
      url: '/web/alerts',
      params: { acknowledged }
    });
  }

  async acknowledgeAlert(alertId: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'POST',
      url: `/web/alerts/${alertId}/acknowledge`
    });
  }

  // ===== ANALYTICS =====
  async getAdvancedAnalytics(type: string, params?: any): Promise<any> {
    return this.request({
      method: 'GET',
      url: `/web/analytics/${type}`,
      params
    });
  }

  async exportData(type: string, format: 'csv' | 'json', params?: any): Promise<Blob> {
    const response = await this.client.get(`/web/export/${type}`, {
      params: { format, ...params },
      responseType: 'blob'
    });
    return response.data;
  }

  // ===== AUTOMATION =====
  async getAutomationRules(): Promise<any[]> {
    return this.request({
      method: 'GET',
      url: '/web/automation/rules'
    });
  }

  async createAutomationRule(rule: any): Promise<{ success: boolean; id: string }> {
    return this.request({
      method: 'POST',
      url: '/web/automation/rules',
      data: rule
    });
  }

  async updateAutomationRule(ruleId: string, updates: any): Promise<{ success: boolean }> {
    return this.request({
      method: 'PUT',
      url: `/web/automation/rules/${ruleId}`,
      data: updates
    });
  }

  async deleteAutomationRule(ruleId: string): Promise<{ success: boolean }> {
    return this.request({
      method: 'DELETE',
      url: `/web/automation/rules/${ruleId}`
    });
  }
}

// Istanza singleton
const apiClient = new ApiClient();

// Export oggetti API specifici
export const authApi = {
  login: (email: string, password: string) => apiClient.login(email, password),
  verifyToken: (token: string) => apiClient.verifyToken(token),
  refreshToken: (token: string) => apiClient.refreshToken(token),
};

export const dashboardApi = {
  getOverview: () => apiClient.getDashboardOverview(),
};

export const botApi = {
  getStatus: () => apiClient.getBotStatus(),
  restart: () => apiClient.restartBot(),
  deployCommands: () => apiClient.deployCommands(),
  sendMessage: (channelId: string, message: string) => apiClient.sendBotMessage(channelId, message),
};

export const shippingApi = {
  getStats: (dateRange?: { start: string; end: string }) => apiClient.getShippingStats(dateRange),
  getQueueStatus: () => apiClient.getQueueStatus(),
  getOrders: (params: any) => apiClient.getShippingOrders(params),
  cancelOrder: (orderId: string) => apiClient.cancelShippingOrder(orderId),
  getAnalytics: () => apiClient.getShippingAnalytics(),
};

export const financeApi = {
  getData: () => apiClient.getFinancialData(),
  updateBudgetLimits: (limits: any) => apiClient.updateBudgetLimits(limits),
  acknowledgeAlert: (alertId: string) => apiClient.acknowledgeFinancialAlert(alertId),
};

export const usersApi = {
  getUsers: (params: any) => apiClient.getBotUsers(params),
  getUser: (userId: string) => apiClient.getBotUser(userId),
  updateCredits: (userId: string, amount: number, reason: string) => 
    apiClient.updateUserCredits(userId, amount, reason),
  blockUser: (userId: string, reason: string) => apiClient.blockUser(userId, reason),
  unblockUser: (userId: string) => apiClient.unblockUser(userId),
};

export const systemApi = {
  getHealth: () => apiClient.getSystemHealth(),
  getLogs: (params: any) => apiClient.getLogs(params),
  getAlerts: (acknowledged?: boolean) => apiClient.getAlerts(acknowledged),
  acknowledgeAlert: (alertId: string) => apiClient.acknowledgeAlert(alertId),
};

export const analyticsApi = {
  getAdvanced: (type: string, params?: any) => apiClient.getAdvancedAnalytics(type, params),
  exportData: (type: string, format: 'csv' | 'json', params?: any) => 
    apiClient.exportData(type, format, params),
};

export const automationApi = {
  getRules: () => apiClient.getAutomationRules(),
  createRule: (rule: any) => apiClient.createAutomationRule(rule),
  updateRule: (ruleId: string, updates: any) => apiClient.updateAutomationRule(ruleId, updates),
  deleteRule: (ruleId: string) => apiClient.deleteAutomationRule(ruleId),
};

export default apiClient;
