import React from 'react';
import { 
  CheckCircleIcon, 
  ExclamationTriangleIcon, 
  XCircleIcon,
  ClockIcon 
} from '@heroicons/react/24/solid';

interface StatusBadgeProps {
  status: 'healthy' | 'degraded' | 'down' | 'online' | 'offline' | 'pending' | 'processing' | 'completed' | 'failed' | 'cancelled';
  text?: string;
  size?: 'sm' | 'md' | 'lg';
  showIcon?: boolean;
}

const StatusBadge: React.FC<StatusBadgeProps> = ({ 
  status, 
  text, 
  size = 'sm', 
  showIcon = true 
}) => {
  const getStatusConfig = (status: string) => {
    switch (status) {
      case 'healthy':
      case 'online':
      case 'completed':
        return {
          className: 'status-online',
          icon: CheckCircleIcon,
          defaultText: 'Healthy'
        };
      case 'degraded':
      case 'pending':
      case 'processing':
        return {
          className: 'status-warning',
          icon: ClockIcon,
          defaultText: 'Degraded'
        };
      case 'down':
      case 'offline':
      case 'failed':
        return {
          className: 'status-offline',
          icon: XCircleIcon,
          defaultText: 'Down'
        };
      case 'cancelled':
        return {
          className: 'inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400',
          icon: XCircleIcon,
          defaultText: 'Cancelled'
        };
      default:
        return {
          className: 'inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400',
          icon: ClockIcon,
          defaultText: 'Unknown'
        };
    }
  };

  const getSizeClasses = (size: string) => {
    switch (size) {
      case 'lg':
        return 'px-3 py-1 text-sm';
      case 'md':
        return 'px-2.5 py-0.5 text-sm';
      case 'sm':
      default:
        return 'px-2 py-0.5 text-xs';
    }
  };

  const config = getStatusConfig(status);
  const Icon = config.icon;
  const displayText = text || config.defaultText;

  return (
    <span className={`${config.className} ${getSizeClasses(size)} inline-flex items-center space-x-1`}>
      {showIcon && <Icon className={`${size === 'lg' ? 'w-4 h-4' : 'w-3 h-3'}`} />}
      <span>{displayText}</span>
    </span>
  );
};

export default StatusBadge;
