import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  ChartBarIcon,
  TruckIcon,
  UsersIcon,
  CurrencyEuroIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  ClockIcon,
  ArrowUpIcon,
  ArrowDownIcon
} from '@heroicons/react/24/outline';
import {
  LineChart,
  Line,
  AreaChart,
  Area,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell
} from 'recharts';
import { dashboardApi, systemApi } from '../services/api';
import { KPI, SystemHealth, Alert } from '../types';
import LoadingSpinner from '../components/LoadingSpinner';
import KPICard from '../components/KPICard';
import StatusBadge from '../components/StatusBadge';
import toast from 'react-hot-toast';

const Dashboard: React.FC = () => {
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [systemHealth, setSystemHealth] = useState<SystemHealth | null>(null);
  const [alerts, setAlerts] = useState<Alert[]>([]);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const fetchDashboardData = async (isRefresh = false) => {
    try {
      if (isRefresh) {
        setRefreshing(true);
      } else {
        setLoading(true);
      }

      const [overview, health, alertsData] = await Promise.allSettled([
        dashboardApi.getOverview(),
        systemApi.getHealth(),
        systemApi.getAlerts(false)
      ]);

      if (overview.status === 'fulfilled') {
        setDashboardData(overview.value);
      }

      if (health.status === 'fulfilled') {
        setSystemHealth(health.value);
      }

      if (alertsData.status === 'fulfilled') {
        setAlerts(alertsData.value);
      }

      if (!isRefresh) {
        toast.success('Dashboard caricata');
      }
    } catch (error) {
      console.error('Errore caricamento dashboard:', error);
      toast.error('Errore caricamento dashboard');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();

    // Auto-refresh ogni 30 secondi
    const interval = setInterval(() => {
      fetchDashboardData(true);
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'healthy': return 'text-success-600';
      case 'degraded': return 'text-warning-600';
      case 'down': return 'text-error-600';
      default: return 'text-gray-600';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'healthy': return CheckCircleIcon;
      case 'degraded': return ExclamationTriangleIcon;
      case 'down': return ExclamationTriangleIcon;
      default: return ClockIcon;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
            Dashboard Overview
          </h1>
          <p className="text-gray-600 dark:text-gray-400">
            Controllo completo del sistema Hyperion
          </p>
        </div>

        <button
          onClick={() => fetchDashboardData(true)}
          disabled={refreshing}
          className="btn-primary flex items-center space-x-2"
        >
          <motion.div
            animate={refreshing ? { rotate: 360 } : {}}
            transition={{ duration: 1, repeat: refreshing ? Infinity : 0 }}
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
          </motion.div>
          <span>{refreshing ? 'Aggiornamento...' : 'Aggiorna'}</span>
        </button>
      </div>

      {/* System Alerts */}
      {alerts.length > 0 && (
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-warning-50 dark:bg-warning-900/20 border border-warning-200 dark:border-warning-800 rounded-lg p-4"
        >
          <div className="flex items-center space-x-3">
            <ExclamationTriangleIcon className="h-5 w-5 text-warning-600" />
            <div>
              <h3 className="text-sm font-medium text-warning-800 dark:text-warning-400">
                {alerts.length} Alert{alerts.length > 1 ? 's' : ''} Attiv{alerts.length > 1 ? 'i' : 'o'}
              </h3>
              <p className="text-xs text-warning-700 dark:text-warning-500 mt-1">
                {alerts[0].message}
                {alerts.length > 1 && ` e altri ${alerts.length - 1}...`}
              </p>
            </div>
          </div>
        </motion.div>
      )}

      {/* KPIs Grid */}
      {dashboardData?.kpis && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {dashboardData.kpis.map((kpi: KPI, index: number) => (
            <motion.div
              key={kpi.label}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <KPICard kpi={kpi} />
            </motion.div>
          ))}
        </div>
      )}

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* System Health */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          className="lg:col-span-1"
        >
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              System Health
            </h3>

            {systemHealth && (
              <div className="space-y-4">
                {/* Overall Status */}
                <div className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-700/50 rounded-lg">
                  <span className="text-sm font-medium">Status Generale</span>
                  <StatusBadge 
                    status={systemHealth.overallStatus}
                    text={systemHealth.overallStatus.toUpperCase()}
                  />
                </div>

                {/* Database */}
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600 dark:text-gray-400">Database</span>
                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500">
                      {systemHealth.database.responseTime}ms
                    </span>
                    <StatusBadge status={systemHealth.database.status} />
                  </div>
                </div>

                {/* External API */}
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600 dark:text-gray-400">Shipping API</span>
                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500">
                      {systemHealth.externalApi.shipping.responseTime}ms
                    </span>
                    <StatusBadge status={systemHealth.externalApi.shipping.status} />
                  </div>
                </div>

                {/* Bot */}
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600 dark:text-gray-400">Discord Bot</span>
                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500">
                      {systemHealth.services.bot.latency}ms
                    </span>
                    <StatusBadge 
                      status={systemHealth.services.bot.online ? 'healthy' : 'down'} 
                      text={systemHealth.services.bot.online ? 'Online' : 'Offline'}
                    />
                  </div>
                </div>
              </div>
            )}
          </div>
        </motion.div>

        {/* Activity Chart */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="lg:col-span-2"
        >
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              Attività Ultime 24h
            </h3>

            <div className="chart-container">
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={dashboardData?.kpis?.[1]?.trend || []}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#374151" opacity={0.3} />
                  <XAxis 
                    dataKey="x" 
                    stroke="#6B7280"
                    fontSize={12}
                  />
                  <YAxis 
                    stroke="#6B7280"
                    fontSize={12}
                  />
                  <Tooltip 
                    contentStyle={{
                      backgroundColor: '#1F2937',
                      border: '1px solid #374151',
                      borderRadius: '8px',
                      color: '#F9FAFB'
                    }}
                  />
                  <Area
                    type="monotone"
                    dataKey="y"
                    stroke="#3B82F6"
                    fill="url(#colorGradient)"
                    strokeWidth={2}
                  />
                  <defs>
                    <linearGradient id="colorGradient" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#3B82F6" stopOpacity={0.3}/>
                      <stop offset="95%" stopColor="#3B82F6" stopOpacity={0}/>
                    </linearGradient>
                  </defs>
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Recent Activity */}
      {dashboardData?.recentActivity && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
        >
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
              Attività Recente
            </h3>

            <div className="space-y-4">
              {dashboardData.recentActivity.map((activity: any, index: number) => (
                <motion.div
                  key={activity.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="flex items-center space-x-4 p-3 hover:bg-gray-50 dark:hover:bg-gray-700/50 rounded-lg transition-colors"
                >
                  <div className={`
                    flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center
                    ${activity.type === 'order_created' ? 'bg-primary-100 text-primary-600' :
                      activity.type === 'order_completed' ? 'bg-success-100 text-success-600' :
                      'bg-warning-100 text-warning-600'}
                  `}>
                    {activity.type === 'order_created' ? (
                      <TruckIcon className="w-4 h-4" />
                    ) : activity.type === 'order_completed' ? (
                      <CheckCircleIcon className="w-4 h-4" />
                    ) : (
                      <ExclamationTriangleIcon className="w-4 h-4" />
                    )}
                  </div>

                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900 dark:text-white">
                      {activity.title}
                    </p>
                    <p className="text-xs text-gray-500 dark:text-gray-400">
                      {activity.description}
                    </p>
                  </div>

                  <div className="flex-shrink-0 text-xs text-gray-400">
                    {new Date(activity.timestamp).toLocaleTimeString('it-IT', {
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default Dashboard;
