import React, { useState, useEffect } from 'react';
import { Outlet, useNavigate, useLocation } from 'react-router-dom';
import {
  HomeIcon,
  TruckIcon,
  UsersIcon,
  CurrencyEuroIcon,
  ChartBarIcon,
  CogIcon,
  DocumentTextIcon,
  BellIcon,
  MagnifyingGlassIcon,
  ComputerDesktopIcon,
  Bars3Icon,
  XMarkIcon
} from '@heroicons/react/24/outline';
import { motion, AnimatePresence } from 'framer-motion';
import useAuth from '../../hooks/useAuth';
import NotificationCenter from '../NotificationCenter';
import CommandPalette from '../CommandPalette/CommandPalette';
import UserMenu from '../UserMenu/UserMenu';
import StatusIndicator from '../StatusIndicator/StatusIndicator';
import { useWebSocket } from '../../hooks/useWebSocket';

interface NavigationItem {
  name: string;
  href: string;
  icon: React.ElementType;
  badge?: number;
  requiredPermission?: string;
  children?: NavigationItem[];
}

const navigation: NavigationItem[] = [
  {
    name: 'Dashboard',
    href: '/',
    icon: HomeIcon,
  },
  {
    name: 'Bot Control',
    href: '/bot',
    icon: ComputerDesktopIcon,
    requiredPermission: 'bot.manage',
  },
  {
    name: 'Shipping',
    href: '/shipping',
    icon: TruckIcon,
    children: [
      { name: 'Overview', href: '/shipping', icon: ChartBarIcon },
      { name: 'Queue', href: '/shipping/queue', icon: DocumentTextIcon },
      { name: 'Orders', href: '/shipping/orders', icon: DocumentTextIcon },
      { name: 'Analytics', href: '/shipping/analytics', icon: ChartBarIcon },
    ],
  },
  {
    name: 'Users',
    href: '/users',
    icon: UsersIcon,
    requiredPermission: 'users.view',
  },
  {
    name: 'Finance',
    href: '/finance',
    icon: CurrencyEuroIcon,
    requiredPermission: 'finance.view',
  },
  {
    name: 'Analytics',
    href: '/analytics',
    icon: ChartBarIcon,
    requiredPermission: 'analytics.view',
  },
  {
    name: 'Logs',
    href: '/logs',
    icon: DocumentTextIcon,
    requiredPermission: 'system.logs',
  },
  {
    name: 'Settings',
    href: '/settings',
    icon: CogIcon,
    requiredPermission: 'system.settings',
  },
];

const DashboardLayout: React.FC = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [commandPaletteOpen, setCommandPaletteOpen] = useState(false);
  const [notificationPanelOpen, setNotificationPanelOpen] = useState(false);

  const { user, hasPermission, logout } = useAuth();
  const { notifications } = useWebSocket();
  const navigate = useNavigate();
  const location = useLocation();

  const unreadNotifications = notifications.filter(n => !n.read).length;

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Ctrl/Cmd + K for command palette
      if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
        e.preventDefault();
        setCommandPaletteOpen(true);
      }

      // Escape to close modals
      if (e.key === 'Escape') {
        setCommandPaletteOpen(false);
        setNotificationPanelOpen(false);
        setSidebarOpen(false);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, []);

  const filteredNavigation = navigation.filter(item => {
    if (item.requiredPermission) {
      return hasPermission(item.requiredPermission);
    }
    return true;
  });

  const isCurrentPath = (href: string) => {
    if (href === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(href);
  };

  const NavigationItem: React.FC<{ item: NavigationItem; isChild?: boolean }> = ({ item, isChild = false }) => {
    const [isExpanded, setIsExpanded] = useState(isCurrentPath(item.href));

    return (
      <div>
        <button
          onClick={() => {
            if (item.children) {
              setIsExpanded(!isExpanded);
            } else {
              navigate(item.href);
              setSidebarOpen(false);
            }
          }}
          className={`
            group flex items-center w-full px-3 py-2 text-sm font-medium rounded-lg transition-all duration-200
            ${isChild ? 'ml-6 pl-6' : ''}
            ${isCurrentPath(item.href) 
              ? 'bg-primary-100 text-primary-900 dark:bg-primary-900/20 dark:text-primary-400' 
              : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700/50'
            }
          `}
        >
          <item.icon className="mr-3 h-5 w-5 flex-shrink-0" />
          <span className="flex-1 text-left">{item.name}</span>
          {item.badge && (
            <span className="ml-2 bg-primary-500 text-white text-xs rounded-full px-2 py-1">
              {item.badge}
            </span>
          )}
          {item.children && (
            <motion.div
              animate={{ rotate: isExpanded ? 90 : 0 }}
              transition={{ duration: 0.2 }}
              className="ml-2"
            >
              <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
              </svg>
            </motion.div>
          )}
        </button>

        <AnimatePresence>
          {item.children && isExpanded && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.2 }}
              className="overflow-hidden"
            >
              <div className="mt-1 space-y-1">
                {item.children.map((child) => (
                  <NavigationItem key={child.href} item={child} isChild />
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      {/* Mobile sidebar overlay */}
      <AnimatePresence>
        {sidebarOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-40 lg:hidden"
          >
            <div 
              className="fixed inset-0 bg-gray-600 bg-opacity-75"
              onClick={() => setSidebarOpen(false)}
            />
          </motion.div>
        )}
      </AnimatePresence>

      {/* Sidebar */}
      <motion.div
        className={`
          fixed inset-y-0 left-0 z-50 w-64 bg-white dark:bg-gray-800 shadow-xl transform transition-transform duration-300 ease-in-out
          lg:translate-x-0 lg:static lg:inset-0
          ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        `}
      >
        <div className="flex flex-col h-full">
          {/* Logo */}
          <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200 dark:border-gray-700">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-primary-500 to-primary-700 rounded-lg flex items-center justify-center">
                <span className="text-white font-bold text-sm">H</span>
              </div>
              <div>
                <h1 className="text-lg font-bold text-gray-900 dark:text-white">Hyperion</h1>
                <p className="text-xs text-gray-500 dark:text-gray-400">Dashboard v2.0</p>
              </div>
            </div>

            <button
              onClick={() => setSidebarOpen(false)}
              className="lg:hidden p-1 rounded-md text-gray-400 hover:text-gray-500"
            >
              <XMarkIcon className="h-6 w-6" />
            </button>
          </div>

          {/* Navigation */}
          <nav className="flex-1 px-4 py-6 space-y-2 overflow-y-auto custom-scrollbar">
            {filteredNavigation.map((item) => (
              <NavigationItem key={item.href} item={item} />
            ))}
          </nav>

          {/* Status & User */}
          <div className="border-t border-gray-200 dark:border-gray-700 p-4 space-y-3">
            <StatusIndicator />
            <UserMenu user={user} onLogout={logout} />
          </div>
        </div>
      </motion.div>

      {/* Main content */}
      <div className="lg:ml-64 flex flex-col min-h-screen">
        {/* Header */}
        <header className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700 sticky top-0 z-30">
          <div className="px-4 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between h-16">
              {/* Left side */}
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => setSidebarOpen(true)}
                  className="lg:hidden p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-700"
                >
                  <Bars3Icon className="h-6 w-6" />
                </button>

                {/* Breadcrumb */}
                <div className="hidden sm:flex items-center space-x-2 text-sm">
                  <span className="text-gray-500 dark:text-gray-400">Dashboard</span>
                  {location.pathname !== '/' && (
                    <>
                      <span className="text-gray-300 dark:text-gray-600">/</span>
                      <span className="text-gray-900 dark:text-gray-100 capitalize">
                        {location.pathname.split('/')[1]}
                      </span>
                    </>
                  )}
                </div>
              </div>

              {/* Right side */}
              <div className="flex items-center space-x-4">
                {/* Search/Command Palette */}
                <button
                  onClick={() => setCommandPaletteOpen(true)}
                  className="p-2 text-gray-400 hover:text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
                  title="Cerca (Ctrl+K)"
                >
                  <MagnifyingGlassIcon className="h-5 w-5" />
                </button>

                {/* Notifications */}
                <button
                  onClick={() => setNotificationPanelOpen(true)}
                  className="relative p-2 text-gray-400 hover:text-gray-500 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
                >
                  <BellIcon className="h-5 w-5" />
                  {unreadNotifications > 0 && (
                    <span className="absolute -top-1 -right-1 h-4 w-4 bg-error-500 text-white text-xs rounded-full flex items-center justify-center">
                      {unreadNotifications > 9 ? '9+' : unreadNotifications}
                    </span>
                  )}
                </button>
              </div>
            </div>
          </div>
        </header>

        {/* Page content */}
        <main className="flex-1 p-6">
          <Outlet />
        </main>
      </div>

      {/* Command Palette */}
      <CommandPalette 
        open={commandPaletteOpen} 
        onClose={() => setCommandPaletteOpen(false)} 
      />

      {/* Notification Center */}
      <NotificationCenter 
        isOpen={notificationPanelOpen} 
        onClose={() => setNotificationPanelOpen(false)}
      />
    </div>
  );
};

export default DashboardLayout;
