import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { EyeIcon, EyeSlashIcon } from '@heroicons/react/24/outline';
import { useNavigate, useLocation } from 'react-router-dom';
import useAuth from '../hooks/useAuth';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);

  const { login, isAuthenticated, isLoading: authLoading } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const from = location.state?.from?.pathname || '/';

  // Redirect se già autenticato
  useEffect(() => {
    if (isAuthenticated && !authLoading) {
      navigate(from, { replace: true });
    }
  }, [isAuthenticated, authLoading, navigate, from]);

  // Load saved credentials
  useEffect(() => {
    const savedEmail = localStorage.getItem('hyperion_saved_email');
    const savedRememberMe = localStorage.getItem('hyperion_remember_me') === 'true';

    if (savedEmail && savedRememberMe) {
      setEmail(savedEmail);
      setRememberMe(true);
    }
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email || !password) {
      toast.error('Email e password sono richiesti');
      return;
    }

    setIsLoading(true);

    try {
      const success = await login(email, password);

      if (success) {
        // Save email if remember me is checked
        if (rememberMe) {
          localStorage.setItem('hyperion_saved_email', email);
          localStorage.setItem('hyperion_remember_me', 'true');
        } else {
          localStorage.removeItem('hyperion_saved_email');
          localStorage.removeItem('hyperion_remember_me');
        }

        navigate(from, { replace: true });
      }
    } catch (error) {
      console.error('Login error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const quickLoginOptions = [
    {
      role: 'Super Admin',
      email: 'admin@hyperion.dev',
      password: 'admin123',
      description: 'Accesso completo a tutte le funzionalità'
    },
    {
      role: 'Admin',
      email: 'user@hyperion.dev', 
      password: 'user123',
      description: 'Gestione shipping e utenti'
    },
    {
      role: 'Viewer',
      email: 'viewer@hyperion.dev',
      password: 'viewer123', 
      description: 'Solo visualizzazione'
    }
  ];

  const handleQuickLogin = (email: string, password: string) => {
    setEmail(email);
    setPassword(password);
  };

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900">
        <LoadingSpinner size="large" text="Verifica autenticazione..." />
      </div>
    );
  }

  return (
    <div className="min-h-screen flex">
      {/* Left side - Login Form */}
