import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { systemApi } from '../../services/api';
import StatusBadge from '../StatusBadge';

const StatusIndicator: React.FC = () => {
  const [systemHealth, setSystemHealth] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSystemHealth = async () => {
      try {
        const health = await systemApi.getHealth();
        setSystemHealth(health);
      } catch (error) {
        console.error('Error fetching system health:', error);
        // Fallback data
        setSystemHealth({
          overallStatus: 'degraded',
          services: {
            bot: { online: false }
          }
        });
      } finally {
        setLoading(false);
      }
    };

    fetchSystemHealth();

    // Update every 30 seconds
    const interval = setInterval(fetchSystemHealth, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="flex items-center space-x-2 p-2 bg-gray-50 dark:bg-gray-700/50 rounded-lg">
        <div className="w-2 h-2 bg-gray-400 rounded-full animate-pulse" />
        <span className="text-xs text-gray-500">Caricamento...</span>
      </div>
    );
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'healthy': return 'bg-success-500';
      case 'degraded': return 'bg-warning-500';
      case 'down': return 'bg-error-500';
      default: return 'bg-gray-500';
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className="p-3 bg-gray-50 dark:bg-gray-700/50 rounded-lg"
    >
      <div className="flex items-center justify-between mb-2">
        <span className="text-xs font-medium text-gray-600 dark:text-gray-400">
          System Status
        </span>
        <div className="flex items-center space-x-2">
          <motion.div
            className={`w-2 h-2 rounded-full ${getStatusColor(systemHealth?.overallStatus)}`}
            animate={{ scale: [1, 1.2, 1] }}
            transition={{ duration: 2, repeat: Infinity }}
          />
          <StatusBadge 
            status={systemHealth?.overallStatus || 'down'} 
            size="sm"
            showIcon={false}
          />
        </div>
      </div>

      <div className="space-y-1 text-xs">
        <div className="flex justify-between">
          <span className="text-gray-500 dark:text-gray-400">Bot:</span>
          <span className={`${systemHealth?.services?.bot?.online ? 'text-success-600' : 'text-error-600'}`}>
            {systemHealth?.services?.bot?.online ? 'Online' : 'Offline'}
          </span>
        </div>
        <div className="flex justify-between">
          <span className="text-gray-500 dark:text-gray-400">API:</span>
          <span className="text-success-600">Healthy</span>
        </div>
      </div>
    </motion.div>
  );
};

export default StatusIndicator;
